	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collApplicationI18ns) {
				foreach ((array) $this->collApplicationI18ns as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collServices) {
				foreach ((array) $this->collServices as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collMarketingPackages) {
				foreach ((array) $this->collMarketingPackages as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collMessageTypes) {
				foreach ((array) $this->collMessageTypes as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collApplicationI18ns = null;
		$this->collServices = null;
		$this->collMarketingPackages = null;
		$this->collMessageTypes = null;
	}


  /**
   * Returns the culture.
   *
   * @return string The culture
   */
  public function getCulture()
  {
    return $this->culture;
  }

  /**
   * Sets the culture.
   *
   * @param string $culture The culture to set
   *
   * @return void
   */
  public function setCulture($culture)
  {
    $this->culture = $culture;
  }

  public function getName($culture = null)
  {
    return $this->getCurrentApplicationI18n($culture)->getName();
  }

  public function setName($value, $culture = null)
  {
    $this->getCurrentApplicationI18n($culture)->setName($value);
  }

  protected $current_i18n = array();

  public function getCurrentApplicationI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = ApplicationI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setApplicationI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setApplicationI18nForCulture(new ApplicationI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setApplicationI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addApplicationI18n($object);
  }


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseApplication:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseApplication::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


} // BaseApplication
