		if ($this->isColumnModified(CommentPeer::ITEM_ID)) $criteria->add(CommentPeer::ITEM_ID, $this->item_id);
		if ($this->isColumnModified(CommentPeer::ITEM_TYPE_ID)) $criteria->add(CommentPeer::ITEM_TYPE_ID, $this->item_type_id);
		if ($this->isColumnModified(CommentPeer::COMMENTER_ID)) $criteria->add(CommentPeer::COMMENTER_ID, $this->commenter_id);
		if ($this->isColumnModified(CommentPeer::COMMENTER_TYPE_ID)) $criteria->add(CommentPeer::COMMENTER_TYPE_ID, $this->commenter_type_id);
		if ($this->isColumnModified(CommentPeer::TEXT)) $criteria->add(CommentPeer::TEXT, $this->text);
		if ($this->isColumnModified(CommentPeer::DELETED_AT)) $criteria->add(CommentPeer::DELETED_AT, $this->deleted_at);
		if ($this->isColumnModified(CommentPeer::CREATED_AT)) $criteria->add(CommentPeer::CREATED_AT, $this->created_at);

		return $criteria;
	}

