	 * @param      PropelPDO $con the connection to use
	 * @return     CompanyBrand
	 */
	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = CompanyBrandPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(CompanyBrandPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(CompanyBrandPeer::DATABASE_NAME);
		$criteria->add(CompanyBrandPeer::ID, $pk);

		$v = CompanyBrandPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
	}

