	/**
	 * Method perform a DELETE on the database, given a Company or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or Company object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).  This includes CASCADE-related rows
	 *				if supported by native driver or if emulated using Propel.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	 public static function doDelete($values, PropelPDO $con = null)
	 {
		if ($con === null) {
			$con = Propel::getConnection(CompanyPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			// invalidate the cache for all objects of this type, since we have no
			// way of knowing (without running a query) what objects should be invalidated
			// from the cache based on this Criteria.
			CompanyPeer::clearInstancePool();

			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof Company) {
			// invalidate the cache for this single object
			CompanyPeer::removeInstanceFromPool($values);
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else {
			// it must be the primary key



			$criteria = new Criteria(self::DATABASE_NAME);
			$criteria->add(CompanyPeer::ID, (array) $values, Criteria::IN);

			foreach ((array) $values as $singleval) {
				// we can invalidate the cache for this single object
				CompanyPeer::removeInstanceFromPool($singleval);
			}
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += CompanyPeer::doOnDeleteCascade($criteria, $con);
			CompanyPeer::doOnDeleteSetNull($criteria, $con);
			
				// Because this db requires some delete cascade/set null emulation, we have to
				// clear the cached instance *after* the emulation has happened (since
				// instances get re-added by the select statement contained therein).
				if ($values instanceof Criteria) {
					CompanyPeer::clearInstancePool();
				} else { // it's a PK or object
					CompanyPeer::removeInstanceFromPool($values);
				}
			
			$affectedRows += BasePeer::doDelete($criteria, $con);

			// invalidate objects in EmailTransactionPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			EmailTransactionPeer::clearInstancePool();

			// invalidate objects in BlocklistPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			BlocklistPeer::clearInstancePool();

			// invalidate objects in CompanyLoginPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			CompanyLoginPeer::clearInstancePool();

			// invalidate objects in CompanyUserPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			CompanyUserPeer::clearInstancePool();

			// invalidate objects in ResumeWorkPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			ResumeWorkPeer::clearInstancePool();

			// invalidate objects in ResumeReferencePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			ResumeReferencePeer::clearInstancePool();

			// invalidate objects in RelationPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			RelationPeer::clearInstancePool();

			// invalidate objects in RelationPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			RelationPeer::clearInstancePool();

			// invalidate objects in ProductGroupPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			ProductGroupPeer::clearInstancePool();

			// invalidate objects in ProductPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			ProductPeer::clearInstancePool();

			// invalidate objects in CompanyBrandPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			CompanyBrandPeer::clearInstancePool();

			// invalidate objects in PlatformAdPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			PlatformAdPeer::clearInstancePool();

			// invalidate objects in B2bLeadPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			B2bLeadPeer::clearInstancePool();

			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * This is a method for emulating ON DELETE CASCADE for DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
	{
		// initialize var to track total num of affected rows
		$affectedRows = 0;

		// first find the objects that are implicated by the $criteria
		$objects = CompanyPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {


			// delete related Blocklist objects
			$c = new Criteria(BlocklistPeer::DATABASE_NAME);
			
			$c->add(BlocklistPeer::COMPANY_ID, $obj->getId());
			$affectedRows += BlocklistPeer::doDelete($c, $con);

			// delete related CompanyLogin objects
			$c = new Criteria(CompanyLoginPeer::DATABASE_NAME);
			
			$c->add(CompanyLoginPeer::COMPANY_ID, $obj->getId());
			$affectedRows += CompanyLoginPeer::doDelete($c, $con);

			// delete related CompanyUser objects
			$c = new Criteria(CompanyUserPeer::DATABASE_NAME);
			
			$c->add(CompanyUserPeer::COMPANY_ID, $obj->getId());
			$affectedRows += CompanyUserPeer::doDelete($c, $con);

			// delete related Relation objects
			$c = new Criteria(RelationPeer::DATABASE_NAME);
			
			$c->add(RelationPeer::COMPANY_ID, $obj->getId());
			$affectedRows += RelationPeer::doDelete($c, $con);

			// delete related Relation objects
			$c = new Criteria(RelationPeer::DATABASE_NAME);
			
			$c->add(RelationPeer::RELATED_COMPANY_ID, $obj->getId());
			$affectedRows += RelationPeer::doDelete($c, $con);

			// delete related ProductGroup objects
			$c = new Criteria(ProductGroupPeer::DATABASE_NAME);
			
			$c->add(ProductGroupPeer::COMPANY_ID, $obj->getId());
			$affectedRows += ProductGroupPeer::doDelete($c, $con);

			// delete related Product objects
			$c = new Criteria(ProductPeer::DATABASE_NAME);
			
			$c->add(ProductPeer::COMPANY_ID, $obj->getId());
			$affectedRows += ProductPeer::doDelete($c, $con);

			// delete related CompanyBrand objects
			$c = new Criteria(CompanyBrandPeer::DATABASE_NAME);
			
			$c->add(CompanyBrandPeer::COMPANY_ID, $obj->getId());
			$affectedRows += CompanyBrandPeer::doDelete($c, $con);

			// delete related B2bLead objects
			$c = new Criteria(B2bLeadPeer::DATABASE_NAME);
			
			$c->add(B2bLeadPeer::COMPANY_ID, $obj->getId());
			$affectedRows += B2bLeadPeer::doDelete($c, $con);
		}
		return $affectedRows;
	}

	/**
