			$updateValues->add(EmailTransactionPeer::RCPNT_COMPANY_ID, null);

					BasePeer::doUpdate($selectCriteria, $updateValues, $con); // use BasePeer because generated Peer doUpdate() methods only update using pkey

			// set fkey col in related ResumeWork rows to NULL
			$selectCriteria = new Criteria(CompanyPeer::DATABASE_NAME);
			$updateValues = new Criteria(CompanyPeer::DATABASE_NAME);
			$selectCriteria->add(ResumeWorkPeer::COMPANY_ID, $obj->getId());
			$updateValues->add(ResumeWorkPeer::COMPANY_ID, null);

