  /**
   * Returns the culture.
   *
   * @return string The culture
   */
  public function getCulture()
  {
    return $this->culture;
  }

  /**
   * Sets the culture.
   *
   * @param string $culture The culture to set
   *
   * @return void
   */
  public function setCulture($culture)
  {
    $this->culture = $culture;
  }

  public function getIntroduction($culture = null)
  {
    return $this->getCurrentCompanyProfileI18n($culture)->getIntroduction();
  }

  public function setIntroduction($value, $culture = null)
  {
    $this->getCurrentCompanyProfileI18n($culture)->setIntroduction($value);
  }

  public function getProductService($culture = null)
  {
    return $this->getCurrentCompanyProfileI18n($culture)->getProductService();
  }

  public function setProductService($value, $culture = null)
  {
    $this->getCurrentCompanyProfileI18n($culture)->setProductService($value);
  }

  protected $current_i18n = array();

  public function getCurrentCompanyProfileI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = CompanyProfileI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setCompanyProfileI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setCompanyProfileI18nForCulture(new CompanyProfileI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setCompanyProfileI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addCompanyProfileI18n($object);
  }


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseCompanyProfile:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseCompanyProfile::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


