	 * @param      Place $l Place
	 * @return     void
	 * @throws     PropelException
	 */
	public function addPlace(Place $l)
	{
		if ($this->collPlaces === null) {
			$this->initPlaces();
		}
		if (!in_array($l, $this->collPlaces, true)) { // only add it if the **same** object is not already associated
			array_push($this->collPlaces, $l);
			$l->setContact($this);
		}
	}


