	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collContactPhones) {
				foreach ((array) $this->collContactPhones as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collContactAddresss) {
				foreach ((array) $this->collContactAddresss as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collUserProfiles) {
				foreach ((array) $this->collUserProfiles as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collCompanyProfiles) {
				foreach ((array) $this->collCompanyProfiles as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collResumes) {
				foreach ((array) $this->collResumes as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collResumeReferences) {
				foreach ((array) $this->collResumeReferences as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collGroups) {
				foreach ((array) $this->collGroups as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collPlaces) {
				foreach ((array) $this->collPlaces as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collContactPhones = null;
		$this->collContactAddresss = null;
		$this->collUserProfiles = null;
		$this->collCompanyProfiles = null;
		$this->collResumes = null;
		$this->collResumeReferences = null;
		$this->collGroups = null;
		$this->collPlaces = null;
	}


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseContact:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseContact::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


