  /**
   * Returns the culture.
   *
   * @return string The culture
   */
  public function getCulture()
  {
    return $this->culture;
  }

  /**
   * Sets the culture.
   *
   * @param string $culture The culture to set
   *
   * @return void
   */
  public function setCulture($culture)
  {
    $this->culture = $culture;
  }

  public function getTitle($culture = null)
  {
    return $this->getCurrentEmailLayoutI18n($culture)->getTitle();
  }

  public function setTitle($value, $culture = null)
  {
    $this->getCurrentEmailLayoutI18n($culture)->setTitle($value);
  }

  public function getContent($culture = null)
  {
    return $this->getCurrentEmailLayoutI18n($culture)->getContent();
  }

  public function setContent($value, $culture = null)
  {
    $this->getCurrentEmailLayoutI18n($culture)->setContent($value);
  }

  public function getDefaults($culture = null)
  {
    return $this->getCurrentEmailLayoutI18n($culture)->getDefaults();
  }

  public function setDefaults($value, $culture = null)
  {
    $this->getCurrentEmailLayoutI18n($culture)->setDefaults($value);
  }

  protected $current_i18n = array();

  public function getCurrentEmailLayoutI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = EmailLayoutI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setEmailLayoutI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setEmailLayoutI18nForCulture(new EmailLayoutI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setEmailLayoutI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addEmailLayoutI18n($object);
  }


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseEmailLayout:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseEmailLayout::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


