  protected $current_i18n = array();

  public function getCurrentEmailTransactionNamespaceI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = EmailTransactionNamespaceI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setEmailTransactionNamespaceI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setEmailTransactionNamespaceI18nForCulture(new EmailTransactionNamespaceI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setEmailTransactionNamespaceI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addEmailTransactionNamespaceI18n($object);
  }


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseEmailTransactionNamespace:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseEmailTransactionNamespace::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


