  }

  public function setName($value, $culture = null)
  {
    $this->getCurrentEventI18n($culture)->setName($value);
  }

  public function getIntroduction($culture = null)
  {
    return $this->getCurrentEventI18n($culture)->getIntroduction();
  }

  public function setIntroduction($value, $culture = null)
  {
    $this->getCurrentEventI18n($culture)->setIntroduction($value);
  }

  protected $current_i18n = array();

  public function getCurrentEventI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = EventI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setEventI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setEventI18nForCulture(new EventI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setEventI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addEventI18n($object);
  }


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseEvent:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseEvent::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


