	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(EventInvitePeer::DATABASE_NAME);

		if ($this->isColumnModified(EventInvitePeer::ID)) $criteria->add(EventInvitePeer::ID, $this->id);
		if ($this->isColumnModified(EventInvitePeer::EVENT_ID)) $criteria->add(EventInvitePeer::EVENT_ID, $this->event_id);
		if ($this->isColumnModified(EventInvitePeer::SUBJECT_ID)) $criteria->add(EventInvitePeer::SUBJECT_ID, $this->subject_id);
		if ($this->isColumnModified(EventInvitePeer::SUBJECT_TYPE_ID)) $criteria->add(EventInvitePeer::SUBJECT_TYPE_ID, $this->subject_type_id);
		if ($this->isColumnModified(EventInvitePeer::INVITER_ID)) $criteria->add(EventInvitePeer::INVITER_ID, $this->inviter_id);
		if ($this->isColumnModified(EventInvitePeer::INVITER_TYPE_ID)) $criteria->add(EventInvitePeer::INVITER_TYPE_ID, $this->inviter_type_id);
		if ($this->isColumnModified(EventInvitePeer::RSVP_STATUS)) $criteria->add(EventInvitePeer::RSVP_STATUS, $this->rsvp_status);

		return $criteria;
	}

