	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setEventId($this->event_id);

		$copyObj->setSubjectId($this->subject_id);

		$copyObj->setSubjectTypeId($this->subject_type_id);

		$copyObj->setInviterId($this->inviter_id);

