			LocationUpdatePeer::clearInstancePool();

			// invalidate objects in LocationUpdatePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			LocationUpdatePeer::clearInstancePool();

			// invalidate objects in PlacePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			PlacePeer::clearInstancePool();

			// invalidate objects in EventPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			EventPeer::clearInstancePool();

