  protected $current_i18n = array();

  public function getCurrentGroupI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = GroupI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setGroupI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setGroupI18nForCulture(new GroupI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setGroupI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addGroupI18n($object);
  }


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseGroup:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseGroup::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


