			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else {
			// it must be the primary key



			$criteria = new Criteria(self::DATABASE_NAME);
			// primary key is composite; we therefore, expect
			// the primary key passed to be an array of pkey
			// values
			if (count($values) == count($values, COUNT_RECURSIVE)) {
				// array is not multi-dimensional
				$values = array($values);
			}

			foreach ($values as $value) {

				$criterion = $criteria->getNewCriterion(HRProfilePeer::ID, $value[0]);
				$criterion->addAnd($criteria->getNewCriterion(HRProfilePeer::OWNER_TYPE_ID, $value[1]));
				$criteria->addOr($criterion);

				// we can invalidate the cache for this single PK
				HRProfilePeer::removeInstanceFromPool($value);
			}
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += HRProfilePeer::doOnDeleteCascade($criteria, $con);
			
				// Because this db requires some delete cascade/set null emulation, we have to
				// clear the cached instance *after* the emulation has happened (since
				// instances get re-added by the select statement contained therein).
				if ($values instanceof Criteria) {
					HRProfilePeer::clearInstancePool();
				} else { // it's a PK or object
					HRProfilePeer::removeInstanceFromPool($values);
				}
			
			$affectedRows += BasePeer::doDelete($criteria, $con);

			// invalidate objects in ResumeFolderPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			ResumeFolderPeer::clearInstancePool();

			// invalidate objects in DatabaseCVPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			DatabaseCVPeer::clearInstancePool();

			// invalidate objects in JobMessageTemplatePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			JobMessageTemplatePeer::clearInstancePool();

			// invalidate objects in JobMessagePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			JobMessagePeer::clearInstancePool();

			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * This is a method for emulating ON DELETE CASCADE for DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
	{
		// initialize var to track total num of affected rows
		$affectedRows = 0;

		// first find the objects that are implicated by the $criteria
		$objects = HRProfilePeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {


			// delete related ResumeFolder objects
			$c = new Criteria(ResumeFolderPeer::DATABASE_NAME);
			
			$c->add(ResumeFolderPeer::HR_PROFILE_ID, $obj->getId());
			$affectedRows += ResumeFolderPeer::doDelete($c, $con);

			// delete related DatabaseCV objects
			$c = new Criteria(DatabaseCVPeer::DATABASE_NAME);
			
			$c->add(DatabaseCVPeer::HR_PROFILE_ID, $obj->getId());
			$affectedRows += DatabaseCVPeer::doDelete($c, $con);

			// delete related JobMessageTemplate objects
			$c = new Criteria(JobMessageTemplatePeer::DATABASE_NAME);
			
			$c->add(JobMessageTemplatePeer::HR_PROFILE_ID, $obj->getId());
			$affectedRows += JobMessageTemplatePeer::doDelete($c, $con);

			// delete related JobMessage objects
			$c = new Criteria(JobMessagePeer::DATABASE_NAME);
			
			$c->add(JobMessagePeer::HR_PROFILE_ID, $obj->getId());
			$affectedRows += JobMessagePeer::doDelete($c, $con);
		}
		return $affectedRows;
	}

	/**
	 * Validates all modified columns of given HRProfile object.
	 * If parameter $columns is either a single column name or an array of column names
	 * than only those columns are validated.
	 *
	 * NOTICE: This does not apply to primary or foreign keys for now.
	 *
	 * @param      HRProfile $obj The object to validate.
	 * @param      mixed $cols Column name or array of column names.
	 *
	 * @return     mixed TRUE if all columns are valid or the error message of the first invalid column.
	 */
	public static function doValidate(HRProfile $obj, $cols = null)
	{
		$columns = array();

		if ($cols) {
			$dbMap = Propel::getDatabaseMap(HRProfilePeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(HRProfilePeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
			}

			foreach ($cols as $colName) {
				if ($tableMap->containsColumn($colName)) {
					$get = 'get' . $tableMap->getColumn($colName)->getPhpName();
					$columns[$colName] = $obj->$get();
				}
			}
		} else {

		}

		$res =  BasePeer::doValidate(HRProfilePeer::DATABASE_NAME, HRProfilePeer::TABLE_NAME, $columns);
    if ($res !== true) {
        $request = sfContext::getInstance()->getRequest();
        foreach ($res as $failed) {
            $col = HRProfilePeer::translateFieldname($failed->getColumn(), BasePeer::TYPE_COLNAME, BasePeer::TYPE_PHPNAME);
        }
    }

    return $res;
	}

	/**
	 * Retrieve object using using composite pkey values.
	 * @param      int $id
	   @param      int $owner_type_id
	   
	 * @param      PropelPDO $con
	 * @return     HRProfile
	 */
	public static function retrieveByPK($id, $owner_type_id, PropelPDO $con = null) {
		$key = serialize(array((string) $id, (string) $owner_type_id));
 		if (null !== ($obj = HRProfilePeer::getInstanceFromPool($key))) {
 			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(HRProfilePeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}
		$criteria = new Criteria(HRProfilePeer::DATABASE_NAME);
		$criteria->add(HRProfilePeer::ID, $id);
		$criteria->add(HRProfilePeer::OWNER_TYPE_ID, $owner_type_id);
		$v = HRProfilePeer::doSelect($criteria, $con);

		return !empty($v) ? $v[0] : null;
	}
