				}
			}
			if ($this->collJobSpecs) {
				foreach ((array) $this->collJobSpecs as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collJobPluss) {
				foreach ((array) $this->collJobPluss as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collJobI18ns = null;
		$this->collUserJobs = null;
		$this->collJobMessages = null;
		$this->collJobLocations = null;
		$this->collJobSpecs = null;
