  protected $current_i18n = array();

  public function getCurrentJobI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = JobI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setJobI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setJobI18nForCulture(new JobI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setJobI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addJobI18n($object);
  }


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseJob:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseJob::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


