			}

			if ($this->aUser !== null) {
				if ($this->aUser->isModified() || $this->aUser->isNew()) {
					$affectedRows += $this->aUser->save($con);
				}
				$this->setUser($this->aUser);
			}

			if ($this->aJob !== null) {
				if ($this->aJob->isModified() || ($this->aJob->getCulture() && $this->aJob->getCurrentJobI18n()->isModified()) || $this->aJob->isNew()) {
					$affectedRows += $this->aJob->save($con);
				}
				$this->setJob($this->aJob);
			}

			if ($this->aJobMessageTemplate !== null) {
				if ($this->aJobMessageTemplate->isModified() || ($this->aJobMessageTemplate->getCulture() && $this->aJobMessageTemplate->getCurrentJobMessageTemplateI18n()->isModified()) || $this->aJobMessageTemplate->isNew()) {
					$affectedRows += $this->aJobMessageTemplate->save($con);
				}
				$this->setJobMessageTemplate($this->aJobMessageTemplate);
			}

			if ($this->isNew() ) {
				$this->modifiedColumns[] = JobMessagePeer::ID;
			}

			// If this object has been modified, then save it to the database.
			if ($this->isModified()) {
				if ($this->isNew()) {
					$pk = JobMessagePeer::doInsert($this, $con);
					$affectedRows += 1; // we are assuming that there is only 1 row per doInsert() which
										 // should always be true here (even though technically
										 // BasePeer::doInsert() can insert multiple rows).

					$this->setId($pk);  //[IMV] update autoincrement primary key

					$this->setNew(false);
				} else {
					$affectedRows += JobMessagePeer::doUpdate($this, $con);
				}

				$this->resetModified(); // [HL] After being saved an object is no longer 'modified'
			}

			$this->alreadyInSave = false;

