	 *
	 * @param      array $columns Array of column names to validate.
	 * @return     mixed <code>true</code> if all validations pass; array of <code>ValidationFailed</code> objets otherwise.
	 */
	protected function doValidate($columns = null)
	{
		if (!$this->alreadyInValidation) {
			$this->alreadyInValidation = true;
			$retval = null;

			$failureMap = array();


			// We call the validate method on the following object(s) if they
			// were passed to this object by their coresponding set
			// method.  This object relates to these object(s) by a
			// foreign key reference.

			if ($this->aHRProfile !== null) {
				if (!$this->aHRProfile->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aHRProfile->getValidationFailures());
				}
			}

			if ($this->aUser !== null) {
				if (!$this->aUser->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aUser->getValidationFailures());
				}
			}

			if ($this->aJob !== null) {
				if (!$this->aJob->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aJob->getValidationFailures());
				}
			}

			if ($this->aJobMessageTemplate !== null) {
				if (!$this->aJobMessageTemplate->validate($columns)) {
					$failureMap = array_merge($failureMap, $this->aJobMessageTemplate->getValidationFailures());
				}
			}


			if (($retval = JobMessagePeer::doValidate($this, $columns)) !== true) {
				$failureMap = array_merge($failureMap, $retval);
			}



			$this->alreadyInValidation = false;
		}

		return (!empty($failureMap) ? $failureMap : true);
	}

