	/**
	 * Method perform an INSERT on the database, given a Login or Criteria object.
	 *
	 * @param      mixed $values Criteria or Login object containing data that is used to create the INSERT statement.
	 * @param      PropelPDO $con the PropelPDO connection to use
	 * @return     mixed The new primary key.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doInsert($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BaseLoginPeer:doInsert:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BaseLoginPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(LoginPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from Login object
		}

		if ($criteria->containsKey(LoginPeer::ID) && $criteria->keyContainsValue(LoginPeer::ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.LoginPeer::ID.')');
		}


		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		try {
			// use transaction because $criteria could contain info
			// for more than one table (I guess, conceivably)
			$con->beginTransaction();
			$pk = BasePeer::doInsert($criteria, $con);
			$con->commit();
		} catch(PropelException $e) {
			$con->rollBack();
			throw $e;
		}

		
    foreach (sfMixer::getCallables('BaseLoginPeer:doInsert:post') as $callable)
    {
      call_user_func($callable, 'BaseLoginPeer', $values, $con, $pk);
    }

    return $pk;
	}

	/**
	 * Method perform an UPDATE on the database, given a Login or Criteria object.
	 *
	 * @param      mixed $values Criteria or Login object containing data that is used to create the UPDATE statement.
	 * @param      PropelPDO $con The connection to use (specify PropelPDO connection object to exert more control over transactions).
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function doUpdate($values, PropelPDO $con = null)
	{

    foreach (sfMixer::getCallables('BaseLoginPeer:doUpdate:pre') as $callable)
    {
      $ret = call_user_func($callable, 'BaseLoginPeer', $values, $con);
      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(LoginPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(LoginPeer::ID);
			$selectCriteria->add(LoginPeer::ID, $criteria->remove(LoginPeer::ID), $comparison);

		} else { // $values is Login object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$ret = BasePeer::doUpdate($selectCriteria, $criteria, $con);
	

    foreach (sfMixer::getCallables('BaseLoginPeer:doUpdate:post') as $callable)
    {
      call_user_func($callable, 'BaseLoginPeer', $values, $con, $ret);
    }

    return $ret;
  }

	/**
	 * Method to DELETE all rows from the EMT_LOGIN table.
	 *
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	public static function doDeleteAll($con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(LoginPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}
		$affectedRows = 0; // initialize var to track total num of affected rows
		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += LoginPeer::doOnDeleteCascade(new Criteria(LoginPeer::DATABASE_NAME), $con);
			$affectedRows += BasePeer::doDeleteAll(LoginPeer::TABLE_NAME, $con);
			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * Method perform a DELETE on the database, given a Login or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or Login object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).  This includes CASCADE-related rows
	 *				if supported by native driver or if emulated using Propel.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	 public static function doDelete($values, PropelPDO $con = null)
	 {
		if ($con === null) {
			$con = Propel::getConnection(LoginPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			// invalidate the cache for all objects of this type, since we have no
			// way of knowing (without running a query) what objects should be invalidated
			// from the cache based on this Criteria.
			LoginPeer::clearInstancePool();

			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof Login) {
			// invalidate the cache for this single object
			LoginPeer::removeInstanceFromPool($values);
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else {
			// it must be the primary key



			$criteria = new Criteria(self::DATABASE_NAME);
			$criteria->add(LoginPeer::ID, (array) $values, Criteria::IN);

			foreach ((array) $values as $singleval) {
				// we can invalidate the cache for this single object
				LoginPeer::removeInstanceFromPool($singleval);
			}
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			$affectedRows += LoginPeer::doOnDeleteCascade($criteria, $con);
			
				// Because this db requires some delete cascade/set null emulation, we have to
				// clear the cached instance *after* the emulation has happened (since
				// instances get re-added by the select statement contained therein).
				if ($values instanceof Criteria) {
					LoginPeer::clearInstancePool();
				} else { // it's a PK or object
					LoginPeer::removeInstanceFromPool($values);
				}
			
			$affectedRows += BasePeer::doDelete($criteria, $con);

			// invalidate objects in UserPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			UserPeer::clearInstancePool();

			// invalidate objects in RoleAssignmentPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			RoleAssignmentPeer::clearInstancePool();

			// invalidate objects in BlocklistPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			BlocklistPeer::clearInstancePool();

			// invalidate objects in CompanyLoginPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			CompanyLoginPeer::clearInstancePool();

			// invalidate objects in PasswordResetRequestPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			PasswordResetRequestPeer::clearInstancePool();

			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * This is a method for emulating ON DELETE CASCADE for DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
	{
		// initialize var to track total num of affected rows
		$affectedRows = 0;

		// first find the objects that are implicated by the $criteria
		$objects = LoginPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {


			// delete related User objects
			$c = new Criteria(UserPeer::DATABASE_NAME);
			
			$c->add(UserPeer::LOGIN_ID, $obj->getId());
			$affectedRows += UserPeer::doDelete($c, $con);

			// delete related RoleAssignment objects
			$c = new Criteria(RoleAssignmentPeer::DATABASE_NAME);
			
			$c->add(RoleAssignmentPeer::LOGIN_ID, $obj->getId());
			$affectedRows += RoleAssignmentPeer::doDelete($c, $con);

			// delete related Blocklist objects
			$c = new Criteria(BlocklistPeer::DATABASE_NAME);
			
			$c->add(BlocklistPeer::LOGIN_ID, $obj->getId());
			$affectedRows += BlocklistPeer::doDelete($c, $con);

			// delete related CompanyLogin objects
			$c = new Criteria(CompanyLoginPeer::DATABASE_NAME);
			
			$c->add(CompanyLoginPeer::LOGIN_ID, $obj->getId());
			$affectedRows += CompanyLoginPeer::doDelete($c, $con);

			// delete related PasswordResetRequest objects
			$c = new Criteria(PasswordResetRequestPeer::DATABASE_NAME);
			
			$c->add(PasswordResetRequestPeer::LOGIN_ID, $obj->getId());
			$affectedRows += PasswordResetRequestPeer::doDelete($c, $con);
		}
		return $affectedRows;
	}

	/**
	 * Validates all modified columns of given Login object.
	 * If parameter $columns is either a single column name or an array of column names
	 * than only those columns are validated.
	 *
	 * NOTICE: This does not apply to primary or foreign keys for now.
	 *
	 * @param      Login $obj The object to validate.
	 * @param      mixed $cols Column name or array of column names.
	 *
	 * @return     mixed TRUE if all columns are valid or the error message of the first invalid column.
	 */
	public static function doValidate(Login $obj, $cols = null)
	{
		$columns = array();

		if ($cols) {
			$dbMap = Propel::getDatabaseMap(LoginPeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(LoginPeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
			}

			foreach ($cols as $colName) {
				if ($tableMap->containsColumn($colName)) {
					$get = 'get' . $tableMap->getColumn($colName)->getPhpName();
					$columns[$colName] = $obj->$get();
				}
			}
		} else {

		}

		$res =  BasePeer::doValidate(LoginPeer::DATABASE_NAME, LoginPeer::TABLE_NAME, $columns);
    if ($res !== true) {
        $request = sfContext::getInstance()->getRequest();
        foreach ($res as $failed) {
            $col = LoginPeer::translateFieldname($failed->getColumn(), BasePeer::TYPE_COLNAME, BasePeer::TYPE_PHPNAME);
        }
    }

    return $res;
	}

	/**
	 * Retrieve a single object by pkey.
	 *
	 * @param      int $pk the primary key.
	 * @param      PropelPDO $con the connection to use
	 * @return     Login
	 */
	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = LoginPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(LoginPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(LoginPeer::DATABASE_NAME);
		$criteria->add(LoginPeer::ID, $pk);

		$v = LoginPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
	}

	/**
	 * Retrieve multiple objects by pkey.
	 *
	 * @param      array $pks List of primary keys
	 * @param      PropelPDO $con the connection to use
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function retrieveByPKs($pks, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(LoginPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$objs = null;
		if (empty($pks)) {
			$objs = array();
		} else {
			$criteria = new Criteria(LoginPeer::DATABASE_NAME);
			$criteria->add(LoginPeer::ID, $pks, Criteria::IN);
			$objs = LoginPeer::doSelect($criteria, $con);
		}
		return $objs;
	}

