  /**
   * Returns the culture.
   *
   * @return string The culture
   */
  public function getCulture()
  {
    return $this->culture;
  }

  /**
   * Sets the culture.
   *
   * @param string $culture The culture to set
   *
   * @return void
   */
  public function setCulture($culture)
  {
    $this->culture = $culture;
  }

  public function getName($culture = null)
  {
    return $this->getCurrentMarketingPackageI18n($culture)->getName();
  }

  public function setName($value, $culture = null)
  {
    $this->getCurrentMarketingPackageI18n($culture)->setName($value);
  }

  public function getDescription($culture = null)
  {
    return $this->getCurrentMarketingPackageI18n($culture)->getDescription();
  }

  public function setDescription($value, $culture = null)
  {
    $this->getCurrentMarketingPackageI18n($culture)->setDescription($value);
  }

  protected $current_i18n = array();

  public function getCurrentMarketingPackageI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = MarketingPackageI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setMarketingPackageI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setMarketingPackageI18nForCulture(new MarketingPackageI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setMarketingPackageI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addMarketingPackageI18n($object);
  }


