	/**
	 * Method perform a DELETE on the database, given a MediaItem or Criteria object OR a primary key value.
	 *
	 * @param      mixed $values Criteria or MediaItem object or primary key or array of primary keys
	 *              which is used to create the DELETE statement
	 * @param      PropelPDO $con the connection to use
	 * @return     int 	The number of affected rows (if supported by underlying database driver).  This includes CASCADE-related rows
	 *				if supported by native driver or if emulated using Propel.
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	 public static function doDelete($values, PropelPDO $con = null)
	 {
		if ($con === null) {
			$con = Propel::getConnection(MediaItemPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			// invalidate the cache for all objects of this type, since we have no
			// way of knowing (without running a query) what objects should be invalidated
			// from the cache based on this Criteria.
			MediaItemPeer::clearInstancePool();

			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof MediaItem) {
			// invalidate the cache for this single object
			MediaItemPeer::removeInstanceFromPool($values);
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else {
			// it must be the primary key



			$criteria = new Criteria(self::DATABASE_NAME);
			$criteria->add(MediaItemPeer::ID, (array) $values, Criteria::IN);

			foreach ((array) $values as $singleval) {
				// we can invalidate the cache for this single object
				MediaItemPeer::removeInstanceFromPool($singleval);
			}
		}

		// Set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		$affectedRows = 0; // initialize var to track total num of affected rows

		try {
			// use transaction because $criteria could contain info
			// for more than one table or we could emulating ON DELETE CASCADE, etc.
			$con->beginTransaction();
			MediaItemPeer::doOnDeleteSetNull($criteria, $con);
			
				// Because this db requires some delete cascade/set null emulation, we have to
				// clear the cached instance *after* the emulation has happened (since
				// instances get re-added by the select statement contained therein).
				if ($values instanceof Criteria) {
					MediaItemPeer::clearInstancePool();
				} else { // it's a PK or object
					MediaItemPeer::removeInstanceFromPool($values);
				}
			
			$affectedRows += BasePeer::doDelete($criteria, $con);

			// invalidate objects in UserProfilePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			UserProfilePeer::clearInstancePool();

			// invalidate objects in ServicePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			ServicePeer::clearInstancePool();

			// invalidate objects in JobPeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			JobPeer::clearInstancePool();

			// invalidate objects in HRProfilePeer instance pool, since one or more of them may be deleted by ON DELETE CASCADE rule.
			HRProfilePeer::clearInstancePool();

			$con->commit();
			return $affectedRows;
		} catch (PropelException $e) {
			$con->rollBack();
			throw $e;
		}
	}

	/**
	 * This is a method for emulating ON DELETE SET NULL DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     void
	 */
	protected static function doOnDeleteSetNull(Criteria $criteria, PropelPDO $con)
	{

		// first find the objects that are implicated by the $criteria
		$objects = MediaItemPeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {

			// set fkey col in related UserProfile rows to NULL
			$selectCriteria = new Criteria(MediaItemPeer::DATABASE_NAME);
			$updateValues = new Criteria(MediaItemPeer::DATABASE_NAME);
			$selectCriteria->add(UserProfilePeer::PHOTO_ID, $obj->getId());
			$updateValues->add(UserProfilePeer::PHOTO_ID, null);

					BasePeer::doUpdate($selectCriteria, $updateValues, $con); // use BasePeer because generated Peer doUpdate() methods only update using pkey

			// set fkey col in related Service rows to NULL
			$selectCriteria = new Criteria(MediaItemPeer::DATABASE_NAME);
			$updateValues = new Criteria(MediaItemPeer::DATABASE_NAME);
			$selectCriteria->add(ServicePeer::IMAGE_ID, $obj->getId());
			$updateValues->add(ServicePeer::IMAGE_ID, null);

					BasePeer::doUpdate($selectCriteria, $updateValues, $con); // use BasePeer because generated Peer doUpdate() methods only update using pkey

			// set fkey col in related Job rows to NULL
			$selectCriteria = new Criteria(MediaItemPeer::DATABASE_NAME);
			$updateValues = new Criteria(MediaItemPeer::DATABASE_NAME);
			$selectCriteria->add(JobPeer::CORPORATE_IMAGE_ID, $obj->getId());
			$updateValues->add(JobPeer::CORPORATE_IMAGE_ID, null);

					BasePeer::doUpdate($selectCriteria, $updateValues, $con); // use BasePeer because generated Peer doUpdate() methods only update using pkey

			// set fkey col in related HRProfile rows to NULL
			$selectCriteria = new Criteria(MediaItemPeer::DATABASE_NAME);
			$updateValues = new Criteria(MediaItemPeer::DATABASE_NAME);
			$selectCriteria->add(HRProfilePeer::LOGO_ID, $obj->getId());
			$updateValues->add(HRProfilePeer::LOGO_ID, null);

					BasePeer::doUpdate($selectCriteria, $updateValues, $con); // use BasePeer because generated Peer doUpdate() methods only update using pkey

		}
	}

	/**
	 * Validates all modified columns of given MediaItem object.
	 * If parameter $columns is either a single column name or an array of column names
	 * than only those columns are validated.
	 *
	 * NOTICE: This does not apply to primary or foreign keys for now.
	 *
	 * @param      MediaItem $obj The object to validate.
	 * @param      mixed $cols Column name or array of column names.
	 *
	 * @return     mixed TRUE if all columns are valid or the error message of the first invalid column.
	 */
	public static function doValidate(MediaItem $obj, $cols = null)
	{
		$columns = array();

		if ($cols) {
			$dbMap = Propel::getDatabaseMap(MediaItemPeer::DATABASE_NAME);
			$tableMap = $dbMap->getTable(MediaItemPeer::TABLE_NAME);

			if (! is_array($cols)) {
				$cols = array($cols);
			}

			foreach ($cols as $colName) {
				if ($tableMap->containsColumn($colName)) {
					$get = 'get' . $tableMap->getColumn($colName)->getPhpName();
					$columns[$colName] = $obj->$get();
				}
			}
		} else {

		}

		$res =  BasePeer::doValidate(MediaItemPeer::DATABASE_NAME, MediaItemPeer::TABLE_NAME, $columns);
    if ($res !== true) {
        $request = sfContext::getInstance()->getRequest();
        foreach ($res as $failed) {
            $col = MediaItemPeer::translateFieldname($failed->getColumn(), BasePeer::TYPE_COLNAME, BasePeer::TYPE_PHPNAME);
        }
    }

    return $res;
	}

	/**
	 * Retrieve a single object by pkey.
	 *
	 * @param      int $pk the primary key.
	 * @param      PropelPDO $con the connection to use
	 * @return     MediaItem
	 */
	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = MediaItemPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(MediaItemPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(MediaItemPeer::DATABASE_NAME);
		$criteria->add(MediaItemPeer::ID, $pk);

		$v = MediaItemPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
	}

	/**
	 * Retrieve multiple objects by pkey.
	 *
	 * @param      array $pks List of primary keys
	 * @param      PropelPDO $con the connection to use
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function retrieveByPKs($pks, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(MediaItemPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$objs = null;
		if (empty($pks)) {
			$objs = array();
		} else {
			$criteria = new Criteria(MediaItemPeer::DATABASE_NAME);
			$criteria->add(MediaItemPeer::ID, $pks, Criteria::IN);
			$objs = MediaItemPeer::doSelect($criteria, $con);
		}
		return $objs;
	}

