      if (false !== $ret)
      {
        return $ret;
      }
    }


		if ($con === null) {
			$con = Propel::getConnection(MessageRecipientPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity
		} else {
			$criteria = $values->buildCriteria(); // build Criteria from MessageRecipient object
		}

		if ($criteria->containsKey(MessageRecipientPeer::ID) && $criteria->keyContainsValue(MessageRecipientPeer::ID) ) {
			throw new PropelException('Cannot insert a value for auto-increment primary key ('.MessageRecipientPeer::ID.')');
