			$c->add(PrivacyNodeTypePeer::ID, $this->payer_type_id);
			$this->aPrivacyNodeType = PrivacyNodeTypePeer::doSelectOne($c, $con);
			/* The following can be used additionally to
			   guarantee the related object contains a reference
			   to this object.  This level of coupling may, however, be
			   undesirable since it could result in an only partially populated collection
			   in the referenced object.
			   $this->aPrivacyNodeType->addPayments($this);
			 */
		}
		return $this->aPrivacyNodeType;
	}

	/**
	 * Declares an association between this object and a CreditAccount object.
	 *
	 * @param      CreditAccount $v
	 * @return     Payment The current object (for fluent API support)
	 * @throws     PropelException
	 */
	public function setCreditAccount(CreditAccount $v = null)
	{
		if ($v === null) {
			$this->setCreditAccountId(NULL);
		} else {
			$this->setCreditAccountId($v->getId());
		}

		$this->aCreditAccount = $v;

		// Add binding for other direction of this n:n relationship.
		// If this object has already been added to the CreditAccount object, it will not be re-added.
		if ($v !== null) {
			$v->addPayment($this);
		}

		return $this;
	}


