		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(PaymentTransactionPeer::ID);
			$selectCriteria->add(PaymentTransactionPeer::ID, $criteria->remove(PaymentTransactionPeer::ID), $comparison);

			$comparison = $criteria->getComparison(PaymentTransactionPeer::GUID);
			$selectCriteria->add(PaymentTransactionPeer::GUID, $criteria->remove(PaymentTransactionPeer::GUID), $comparison);

		} else { // $values is PaymentTransaction object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
