	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collPlaceI18ns) {
				foreach ((array) $this->collPlaceI18ns as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collEvents) {
				foreach ((array) $this->collEvents as $o) {
					$o->clearAllReferences($deep);
				}
			}
		} // if ($deep)

		$this->collPlaceI18ns = null;
		$this->collEvents = null;
			$this->aPrivacyNodeType = null;
			$this->aPlaceType = null;
			$this->aGeonameCity = null;
			$this->aContact = null;
	}


  /**
   * Returns the culture.
   *
   * @return string The culture
   */
  public function getCulture()
  {
    return $this->culture;
  }

  /**
   * Sets the culture.
   *
   * @param string $culture The culture to set
   *
   * @return void
   */
  public function setCulture($culture)
  {
    $this->culture = $culture;
  }

  public function getName($culture = null)
  {
    return $this->getCurrentPlaceI18n($culture)->getName();
  }

  public function setName($value, $culture = null)
  {
    $this->getCurrentPlaceI18n($culture)->setName($value);
  }

  public function getIntroduction($culture = null)
  {
    return $this->getCurrentPlaceI18n($culture)->getIntroduction();
  }

  public function setIntroduction($value, $culture = null)
  {
    $this->getCurrentPlaceI18n($culture)->setIntroduction($value);
  }

  protected $current_i18n = array();

  public function getCurrentPlaceI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = PlaceI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setPlaceI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setPlaceI18nForCulture(new PlaceI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setPlaceI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addPlaceI18n($object);
  }


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BasePlace:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BasePlace::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


} // BasePlace
