  public static function doSelectWithI18n(Criteria $c, $culture = null, PropelPDO $con = null)
  {
    // we're going to modify criteria, so copy it first
    $c = clone $c;
    if ($culture === null)
    {
      $culture = sfPropel::getDefaultCulture();
    }


    foreach (sfMixer::getCallables('BasePlacePeer:doSelectJoin:doSelectJoin') as $callable)
    {
      call_user_func($callable, 'BasePlacePeer', $c, $con);
    }


    // Set the correct dbName if it has not been overridden
    if ($c->getDbName() == Propel::getDefaultDB())
    {
      $c->setDbName(self::DATABASE_NAME);
    }

    PlacePeer::addSelectColumns($c);
    $startcol = (PlacePeer::NUM_COLUMNS - PlacePeer::NUM_LAZY_LOAD_COLUMNS);

    PlaceI18nPeer::addSelectColumns($c);

    $c->addJoin(PlacePeer::ID, PlaceI18nPeer::ID);
    $c->add(PlaceI18nPeer::CULTURE, $culture);

    $stmt = BasePeer::doSelect($c, $con);
    $results = array();

    while($row = $stmt->fetch(PDO::FETCH_NUM)) {

      $omClass = PlacePeer::getOMClass();

      $cls = Propel::importClass($omClass);
      $obj1 = new $cls();
      $obj1->hydrate($row);
      $obj1->setCulture($culture);

      $omClass = PlaceI18nPeer::getOMClass();

      $cls = Propel::importClass($omClass);
      $obj2 = new $cls();
      $obj2->hydrate($row, $startcol);

      $obj1->setPlaceI18nForCulture($obj2, $culture);
      $obj2->setPlace($obj1);

      $results[] = $obj1;
    }
    return $results;
  }


