	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(PollItemPeer::DATABASE_NAME);

		if ($this->isColumnModified(PollItemPeer::ID)) $criteria->add(PollItemPeer::ID, $this->id);
		if ($this->isColumnModified(PollItemPeer::GUID)) $criteria->add(PollItemPeer::GUID, $this->guid);
		if ($this->isColumnModified(PollItemPeer::POLL_ID)) $criteria->add(PollItemPeer::POLL_ID, $this->poll_id);
		if ($this->isColumnModified(PollItemPeer::DISPLAY_NAME)) $criteria->add(PollItemPeer::DISPLAY_NAME, $this->display_name);
		if ($this->isColumnModified(PollItemPeer::SEQUENCE_ID)) $criteria->add(PollItemPeer::SEQUENCE_ID, $this->sequence_id);
		if ($this->isColumnModified(PollItemPeer::CREATED_AT)) $criteria->add(PollItemPeer::CREATED_AT, $this->created_at);
		if ($this->isColumnModified(PollItemPeer::UPDATED_AT)) $criteria->add(PollItemPeer::UPDATED_AT, $this->updated_at);

		return $criteria;
	}

