	/**
	 * Declares an association between this object and a Poll object.
	 *
	 * @param      Poll $v
	 * @return     PollItem The current object (for fluent API support)
	 * @throws     PropelException
	 */
	public function setPoll(Poll $v = null)
	{
		if ($v === null) {
			$this->setPollId(NULL);
		} else {
			$this->setPollId($v->getId());
		}

		$this->aPoll = $v;

		// Add binding for other direction of this n:n relationship.
		// If this object has already been added to the Poll object, it will not be re-added.
		if ($v !== null) {
			$v->addPollItem($this);
		}

		return $this;
	}


	/**
	 * Get the associated Poll object
	 *
	 * @param      PropelPDO Optional Connection object.
	 * @return     Poll The associated Poll object.
	 * @throws     PropelException
	 */
	public function getPoll(PropelPDO $con = null)
	{
		if ($this->aPoll === null && ($this->poll_id !== null)) {
			$c = new Criteria(PollPeer::DATABASE_NAME);
			$c->add(PollPeer::ID, $this->poll_id);
			$this->aPoll = PollPeer::doSelectOne($c, $con);
			/* The following can be used additionally to
			   guarantee the related object contains a reference
			   to this object.  This level of coupling may, however, be
			   undesirable since it could result in an only partially populated collection
			   in the referenced object.
			   $this->aPoll->addPollItems($this);
			 */
		}
		return $this->aPoll;
	}

