	/**
	 * Gets an array of PollVote objects which contain a foreign key that references this object.
	 *
	 * If this collection has already been initialized with an identical Criteria, it returns the collection.
	 * Otherwise if this PollItem has previously been saved, it will retrieve
	 * related PollVotes from storage. If this PollItem is new, it will return
	 * an empty collection or the current collection, the criteria is ignored on a new object.
	 *
	 * @param      PropelPDO $con
	 * @param      Criteria $criteria
	 * @return     array PollVote[]
	 * @throws     PropelException
	 */
	public function getPollVotes($criteria = null, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(PollItemPeer::DATABASE_NAME);
		}
		elseif ($criteria instanceof Criteria)
		{
			$criteria = clone $criteria;
		}

		if ($this->collPollVotes === null) {
			if ($this->isNew()) {
			   $this->collPollVotes = array();
			} else {

				$criteria->add(PollVotePeer::ITEM_ID, $this->id);

				PollVotePeer::addSelectColumns($criteria);
				$this->collPollVotes = PollVotePeer::doSelect($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return the collection.


				$criteria->add(PollVotePeer::ITEM_ID, $this->id);

				PollVotePeer::addSelectColumns($criteria);
				if (!isset($this->lastPollVoteCriteria) || !$this->lastPollVoteCriteria->equals($criteria)) {
					$this->collPollVotes = PollVotePeer::doSelect($criteria, $con);
				}
			}
		}
		$this->lastPollVoteCriteria = $criteria;
		return $this->collPollVotes;
	}

	/**
	 * Returns the number of related PollVote objects.
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct
	 * @param      PropelPDO $con
	 * @return     int Count of related PollVote objects.
	 * @throws     PropelException
	 */
	public function countPollVotes(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(PollItemPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collPollVotes === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(PollVotePeer::ITEM_ID, $this->id);

				$count = PollVotePeer::doCount($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(PollVotePeer::ITEM_ID, $this->id);

				if (!isset($this->lastPollVoteCriteria) || !$this->lastPollVoteCriteria->equals($criteria)) {
					$count = PollVotePeer::doCount($criteria, $con);
				} else {
					$count = count($this->collPollVotes);
				}
			} else {
				$count = count($this->collPollVotes);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a PollVote object to this object
	 * through the PollVote foreign key attribute.
	 *
	 * @param      PollVote $l PollVote
	 * @return     void
	 * @throws     PropelException
	 */
	public function addPollVote(PollVote $l)
	{
		if ($this->collPollVotes === null) {
			$this->initPollVotes();
		}
		if (!in_array($l, $this->collPollVotes, true)) { // only add it if the **same** object is not already associated
			array_push($this->collPollVotes, $l);
			$l->setPollItem($this);
		}
	}


