			$criteria->add(PollVotePeer::ITEM_ID, $this->id);

			if (!isset($this->lastPollVoteCriteria) || !$this->lastPollVoteCriteria->equals($criteria)) {
				$this->collPollVotes = PollVotePeer::doSelectJoinClientUser($criteria, $con, $join_behavior);
