   *     rethrown wrapped into a PropelException.
   */
  public static function doSelectWithI18n(Criteria $c, $culture = null, PropelPDO $con = null)
  {
    // we're going to modify criteria, so copy it first
    $c = clone $c;
    if ($culture === null)
    {
      $culture = sfPropel::getDefaultCulture();
    }


    foreach (sfMixer::getCallables('BaseProductAttrPeer:doSelectJoin:doSelectJoin') as $callable)
    {
      call_user_func($callable, 'BaseProductAttrPeer', $c, $con);
    }


    // Set the correct dbName if it has not been overridden
    if ($c->getDbName() == Propel::getDefaultDB())
    {
      $c->setDbName(self::DATABASE_NAME);
    }

    ProductAttrPeer::addSelectColumns($c);
    $startcol = (ProductAttrPeer::NUM_COLUMNS - ProductAttrPeer::NUM_LAZY_LOAD_COLUMNS);

    ProductAttrI18nPeer::addSelectColumns($c);

    $c->addJoin(ProductAttrPeer::ID, ProductAttrI18nPeer::ID);
    $c->add(ProductAttrI18nPeer::CULTURE, $culture);

    $stmt = BasePeer::doSelect($c, $con);
    $results = array();

    while($row = $stmt->fetch(PDO::FETCH_NUM)) {

      $omClass = ProductAttrPeer::getOMClass();

      $cls = Propel::importClass($omClass);
      $obj1 = new $cls();
      $obj1->hydrate($row);
      $obj1->setCulture($culture);

      $omClass = ProductAttrI18nPeer::getOMClass();

      $cls = Propel::importClass($omClass);
      $obj2 = new $cls();
      $obj2->hydrate($row, $startcol);

      $obj1->setProductAttrI18nForCulture($obj2, $culture);
      $obj2->setProductAttr($obj1);

      $results[] = $obj1;
    }
    return $results;
  }


