		if ($this->isColumnModified(PublicationI18nPeer::CULTURE)) $criteria->add(PublicationI18nPeer::CULTURE, $this->culture);
		if ($this->isColumnModified(PublicationI18nPeer::TITLE)) $criteria->add(PublicationI18nPeer::TITLE, $this->title);
		if ($this->isColumnModified(PublicationI18nPeer::STRIPPED_TITLE)) $criteria->add(PublicationI18nPeer::STRIPPED_TITLE, $this->stripped_title);
		if ($this->isColumnModified(PublicationI18nPeer::SUMMARY)) $criteria->add(PublicationI18nPeer::SUMMARY, $this->summary);
		if ($this->isColumnModified(PublicationI18nPeer::INTRODUCTION)) $criteria->add(PublicationI18nPeer::INTRODUCTION, $this->introduction);
		if ($this->isColumnModified(PublicationI18nPeer::CONTENT)) $criteria->add(PublicationI18nPeer::CONTENT, $this->content);
		if ($this->isColumnModified(PublicationI18nPeer::I18N_ACTIVE)) $criteria->add(PublicationI18nPeer::I18N_ACTIVE, $this->i18n_active);
		if ($this->isColumnModified(PublicationI18nPeer::I18N_CREATED_AT)) $criteria->add(PublicationI18nPeer::I18N_CREATED_AT, $this->i18n_created_at);
		if ($this->isColumnModified(PublicationI18nPeer::I18N_UPDATED_AT)) $criteria->add(PublicationI18nPeer::I18N_UPDATED_AT, $this->i18n_updated_at);
		if ($this->isColumnModified(PublicationI18nPeer::SHORT_TITLE)) $criteria->add(PublicationI18nPeer::SHORT_TITLE, $this->short_title);

		return $criteria;
	}

