  /**
   * Selects a collection of Publication objects pre-filled with their i18n objects.
   *
   * @return array Array of Publication objects.
   * @throws PropelException Any exceptions caught during processing will be
   *     rethrown wrapped into a PropelException.
   */
  public static function doSelectWithI18n(Criteria $c, $culture = null, PropelPDO $con = null)
  {
    // we're going to modify criteria, so copy it first
    $c = clone $c;
    if ($culture === null)
    {
      $culture = sfPropel::getDefaultCulture();
    }


    foreach (sfMixer::getCallables('BasePublicationPeer:doSelectJoin:doSelectJoin') as $callable)
    {
      call_user_func($callable, 'BasePublicationPeer', $c, $con);
    }


    // Set the correct dbName if it has not been overridden
    if ($c->getDbName() == Propel::getDefaultDB())
    {
      $c->setDbName(self::DATABASE_NAME);
    }

    PublicationPeer::addSelectColumns($c);
    $startcol = (PublicationPeer::NUM_COLUMNS - PublicationPeer::NUM_LAZY_LOAD_COLUMNS);

    PublicationI18nPeer::addSelectColumns($c);

    $c->addJoin(PublicationPeer::ID, PublicationI18nPeer::ID);
    $c->add(PublicationI18nPeer::CULTURE, $culture);

    $stmt = BasePeer::doSelect($c, $con);
    $results = array();

    while($row = $stmt->fetch(PDO::FETCH_NUM)) {

      $omClass = PublicationPeer::getOMClass();

      $cls = Propel::importClass($omClass);
      $obj1 = new $cls();
      $obj1->hydrate($row);
      $obj1->setCulture($culture);

      $omClass = PublicationI18nPeer::getOMClass();

      $cls = Propel::importClass($omClass);
      $obj2 = new $cls();
      $obj2->hydrate($row, $startcol);

      $obj1->setPublicationI18nForCulture($obj2, $culture);
      $obj2->setPublication($obj1);

      $results[] = $obj1;
    }
    return $results;
  }


