			$con->beginTransaction();
			$affectedRows += ResumePeer::doOnDeleteCascade(new Criteria(ResumePeer::DATABASE_NAME), $con);
			$affectedRows += BasePeer::doDeleteAll(ResumePeer::TABLE_NAME, $con);
