		}
	}

	/**
	 * This is a method for emulating ON DELETE CASCADE for DBs that don't support this
	 * feature (like MySQL or SQLite).
	 *
	 * This method is not very speedy because it must perform a query first to get
	 * the implicated records and then perform the deletes by calling those Peer classes.
	 *
	 * This method should be used within a transaction if possible.
	 *
	 * @param      Criteria $criteria
	 * @param      PropelPDO $con
	 * @return     int The number of affected rows (if supported by underlying database driver).
	 */
	protected static function doOnDeleteCascade(Criteria $criteria, PropelPDO $con)
	{
		// initialize var to track total num of affected rows
		$affectedRows = 0;

		// first find the objects that are implicated by the $criteria
		$objects = ResumePeer::doSelect($criteria, $con);
		foreach ($objects as $obj) {


			// delete related ResumeCourse objects
			$c = new Criteria(ResumeCoursePeer::DATABASE_NAME);
			
			$c->add(ResumeCoursePeer::RESUME_ID, $obj->getId());
			$affectedRows += ResumeCoursePeer::doDelete($c, $con);

			// delete related ResumeLanguage objects
			$c = new Criteria(ResumeLanguagePeer::DATABASE_NAME);
			
			$c->add(ResumeLanguagePeer::RESUME_ID, $obj->getId());
			$affectedRows += ResumeLanguagePeer::doDelete($c, $con);

			// delete related ResumeSchool objects
			$c = new Criteria(ResumeSchoolPeer::DATABASE_NAME);
			
			$c->add(ResumeSchoolPeer::RESUME_ID, $obj->getId());
			$affectedRows += ResumeSchoolPeer::doDelete($c, $con);

			// delete related ResumeWork objects
			$c = new Criteria(ResumeWorkPeer::DATABASE_NAME);
			
			$c->add(ResumeWorkPeer::RESUME_ID, $obj->getId());
			$affectedRows += ResumeWorkPeer::doDelete($c, $con);

			// delete related ResumeSkill objects
			$c = new Criteria(ResumeSkillPeer::DATABASE_NAME);
			
			$c->add(ResumeSkillPeer::RESUME_ID, $obj->getId());
			$affectedRows += ResumeSkillPeer::doDelete($c, $con);

			// delete related ResumePublication objects
			$c = new Criteria(ResumePublicationPeer::DATABASE_NAME);
			
			$c->add(ResumePublicationPeer::RESUME_ID, $obj->getId());
			$affectedRows += ResumePublicationPeer::doDelete($c, $con);

			// delete related ResumeAward objects
			$c = new Criteria(ResumeAwardPeer::DATABASE_NAME);
			
			$c->add(ResumeAwardPeer::RESUME_ID, $obj->getId());
			$affectedRows += ResumeAwardPeer::doDelete($c, $con);

			// delete related ResumeReference objects
			$c = new Criteria(ResumeReferencePeer::DATABASE_NAME);
			
			$c->add(ResumeReferencePeer::RESUME_ID, $obj->getId());
			$affectedRows += ResumeReferencePeer::doDelete($c, $con);

			// delete related ResumeOrganisation objects
			$c = new Criteria(ResumeOrganisationPeer::DATABASE_NAME);
			
			$c->add(ResumeOrganisationPeer::RESUME_ID, $obj->getId());
			$affectedRows += ResumeOrganisationPeer::doDelete($c, $con);

			// delete related ClassifiedResume objects
			$c = new Criteria(ClassifiedResumePeer::DATABASE_NAME);
			
			$c->add(ClassifiedResumePeer::RESUME_ID, $obj->getId());
			$affectedRows += ClassifiedResumePeer::doDelete($c, $con);

			// delete related DatabaseCV objects
			$c = new Criteria(DatabaseCVPeer::DATABASE_NAME);
			
			$c->add(DatabaseCVPeer::RESUME_ID, $obj->getId());
			$affectedRows += DatabaseCVPeer::doDelete($c, $con);
		}
		return $affectedRows;
	}

	/**
