		if (array_key_exists($keys[0], $arr)) $this->setId($arr[$keys[0]]);
		if (array_key_exists($keys[1], $arr)) $this->setObjectTypeId($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setSubjectTypeId($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setIsRelationType($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setBadge($arr[$keys[4]]);
	}

	/**
