	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(RoleMatrixPeer::DATABASE_NAME);

		if ($this->isColumnModified(RoleMatrixPeer::ID)) $criteria->add(RoleMatrixPeer::ID, $this->id);
		if ($this->isColumnModified(RoleMatrixPeer::OBJECT_TYPE_ID)) $criteria->add(RoleMatrixPeer::OBJECT_TYPE_ID, $this->object_type_id);
		if ($this->isColumnModified(RoleMatrixPeer::SUBJECT_TYPE_ID)) $criteria->add(RoleMatrixPeer::SUBJECT_TYPE_ID, $this->subject_type_id);
		if ($this->isColumnModified(RoleMatrixPeer::IS_RELATION_TYPE)) $criteria->add(RoleMatrixPeer::IS_RELATION_TYPE, $this->is_relation_type);
		if ($this->isColumnModified(RoleMatrixPeer::BADGE)) $criteria->add(RoleMatrixPeer::BADGE, $this->badge);

		return $criteria;
	}

