    if (!$callable = sfMixer::getCallable('BaseServicePrice:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseServicePrice::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


} // BaseServicePrice
