  /**
   * Returns the culture.
   *
   * @return string The culture
   */
  public function getCulture()
  {
    return $this->culture;
  }

  /**
   * Sets the culture.
   *
   * @param string $culture The culture to set
   *
   * @return void
   */
  public function setCulture($culture)
  {
    $this->culture = $culture;
  }

  public function getName($culture = null)
  {
    return $this->getCurrentSkillCategoryI18n($culture)->getName();
  }

  public function setName($value, $culture = null)
  {
    $this->getCurrentSkillCategoryI18n($culture)->setName($value);
  }

  protected $current_i18n = array();

  public function getCurrentSkillCategoryI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = SkillCategoryI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setSkillCategoryI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setSkillCategoryI18nForCulture(new SkillCategoryI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setSkillCategoryI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addSkillCategoryI18n($object);
  }


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseSkillCategory:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseSkillCategory::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


