  /**
   * Returns the culture.
   *
   * @return string The culture
   */
  public function getCulture()
  {
    return $this->culture;
  }

  /**
   * Sets the culture.
   *
   * @param string $culture The culture to set
   *
   * @return void
   */
  public function setCulture($culture)
  {
    $this->culture = $culture;
  }

  public function getName($culture = null)
  {
    return $this->getCurrentTradeExpertI18n($culture)->getName();
  }

  public function setName($value, $culture = null)
  {
    $this->getCurrentTradeExpertI18n($culture)->setName($value);
  }

  public function getStrippedName($culture = null)
  {
    return $this->getCurrentTradeExpertI18n($culture)->getStrippedName();
  }

  public function setStrippedName($value, $culture = null)
  {
    $this->getCurrentTradeExpertI18n($culture)->setStrippedName($value);
  }

  public function getIntroduction($culture = null)
  {
    return $this->getCurrentTradeExpertI18n($culture)->getIntroduction();
  }

  public function setIntroduction($value, $culture = null)
  {
    $this->getCurrentTradeExpertI18n($culture)->setIntroduction($value);
  }

  protected $current_i18n = array();

  public function getCurrentTradeExpertI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = TradeExpertI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setTradeExpertI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setTradeExpertI18nForCulture(new TradeExpertI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setTradeExpertI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addTradeExpertI18n($object);
  }


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseTradeExpert:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseTradeExpert::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


