	 * @param      PropelPDO $con the connection to use
	 * @return     TradeExpertArea
	 */
	public static function retrieveByPK($pk, PropelPDO $con = null)
	{

		if (null !== ($obj = TradeExpertAreaPeer::getInstanceFromPool((string) $pk))) {
			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(TradeExpertAreaPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$criteria = new Criteria(TradeExpertAreaPeer::DATABASE_NAME);
		$criteria->add(TradeExpertAreaPeer::ID, $pk);

		$v = TradeExpertAreaPeer::doSelect($criteria, $con);

		return !empty($v) > 0 ? $v[0] : null;
