	 * @param      PropelPDO $con the connection to use
	 * @throws     PropelException Any exceptions caught during processing will be
	 *		 rethrown wrapped into a PropelException.
	 */
	public static function retrieveByPKs($pks, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(TradeExpertAreaPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}

		$objs = null;
		if (empty($pks)) {
			$objs = array();
		} else {
			$criteria = new Criteria(TradeExpertAreaPeer::DATABASE_NAME);
			$criteria->add(TradeExpertAreaPeer::ID, $pks, Criteria::IN);
			$objs = TradeExpertAreaPeer::doSelect($criteria, $con);
		}
		return $objs;
	}

