				if (!isset($this->lastTranslatorI18nCriteria) || !$this->lastTranslatorI18nCriteria->equals($criteria)) {
					$this->collTranslatorI18ns = TranslatorI18nPeer::doSelect($criteria, $con);
				}
			}
		}
		$this->lastTranslatorI18nCriteria = $criteria;
		return $this->collTranslatorI18ns;
	}

	/**
	 * Returns the number of related TranslatorI18n objects.
	 *
	 * @param      Criteria $criteria
	 * @param      boolean $distinct
	 * @param      PropelPDO $con
	 * @return     int Count of related TranslatorI18n objects.
	 * @throws     PropelException
	 */
	public function countTranslatorI18ns(Criteria $criteria = null, $distinct = false, PropelPDO $con = null)
	{
		if ($criteria === null) {
			$criteria = new Criteria(TranslatorPeer::DATABASE_NAME);
		} else {
			$criteria = clone $criteria;
		}

		if ($distinct) {
			$criteria->setDistinct();
		}

		$count = null;

		if ($this->collTranslatorI18ns === null) {
			if ($this->isNew()) {
				$count = 0;
			} else {

				$criteria->add(TranslatorI18nPeer::ID, $this->id);

				$count = TranslatorI18nPeer::doCount($criteria, $con);
			}
		} else {
			// criteria has no effect for a new object
			if (!$this->isNew()) {
				// the following code is to determine if a new query is
				// called for.  If the criteria is the same as the last
				// one, just return count of the collection.


				$criteria->add(TranslatorI18nPeer::ID, $this->id);

				if (!isset($this->lastTranslatorI18nCriteria) || !$this->lastTranslatorI18nCriteria->equals($criteria)) {
					$count = TranslatorI18nPeer::doCount($criteria, $con);
				} else {
					$count = count($this->collTranslatorI18ns);
				}
			} else {
				$count = count($this->collTranslatorI18ns);
			}
		}
		return $count;
	}

	/**
	 * Method called to associate a TranslatorI18n object to this object
	 * through the TranslatorI18n foreign key attribute.
	 *
	 * @param      TranslatorI18n $l TranslatorI18n
	 * @return     void
	 * @throws     PropelException
	 */
	public function addTranslatorI18n(TranslatorI18n $l)
	{
		if ($this->collTranslatorI18ns === null) {
			$this->initTranslatorI18ns();
		}
		if (!in_array($l, $this->collTranslatorI18ns, true)) { // only add it if the **same** object is not already associated
			array_push($this->collTranslatorI18ns, $l);
			$l->setTranslator($this);
		}
	}

