	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
			if ($this->collTranslatorsRelatedByCorporateAccount) {
				foreach ((array) $this->collTranslatorsRelatedByCorporateAccount as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->collTranslatorI18ns) {
				foreach ((array) $this->collTranslatorI18ns as $o) {
					$o->clearAllReferences($deep);
				}
			}
			if ($this->singleTranslatorLanguage) {
				$this->singleTranslatorLanguage->clearAllReferences($deep);
			}
			if ($this->singleTranslatorCouple) {
				$this->singleTranslatorCouple->clearAllReferences($deep);
			}
		} // if ($deep)

		$this->collTranslatorsRelatedByCorporateAccount = null;
		$this->collTranslatorI18ns = null;
		$this->singleTranslatorLanguage = null;
		$this->singleTranslatorCouple = null;
			$this->aTranslatorRelatedByCorporateAccount = null;
			$this->aPrivacyNodeType = null;
	}


  /**
   * Returns the culture.
   *
   * @return string The culture
   */
  public function getCulture()
  {
    return $this->culture;
  }

  /**
   * Sets the culture.
   *
   * @param string $culture The culture to set
   *
   * @return void
   */
  public function setCulture($culture)
  {
    $this->culture = $culture;
  }

  public function getName($culture = null)
  {
    return $this->getCurrentTranslatorI18n($culture)->getName();
  }

  public function setName($value, $culture = null)
  {
    $this->getCurrentTranslatorI18n($culture)->setName($value);
  }

  public function getStrippedName($culture = null)
  {
    return $this->getCurrentTranslatorI18n($culture)->getStrippedName();
  }

  public function setStrippedName($value, $culture = null)
  {
    $this->getCurrentTranslatorI18n($culture)->setStrippedName($value);
  }

  public function getIntroduction($culture = null)
  {
    return $this->getCurrentTranslatorI18n($culture)->getIntroduction();
  }

  public function setIntroduction($value, $culture = null)
  {
    $this->getCurrentTranslatorI18n($culture)->setIntroduction($value);
  }

  protected $current_i18n = array();

  public function getCurrentTranslatorI18n($culture = null)
  {
    if (is_null($culture))
    {
      $culture = is_null($this->culture) ? sfPropel::getDefaultCulture() : $this->culture;
    }

    if (!isset($this->current_i18n[$culture]))
    {
      $obj = TranslatorI18nPeer::retrieveByPK($this->getId(), $culture);
      if ($obj)
      {
        $this->setTranslatorI18nForCulture($obj, $culture);
      }
      else
      {
        $this->setTranslatorI18nForCulture(new TranslatorI18n(), $culture);
        $this->current_i18n[$culture]->setCulture($culture);
      }
    }

    return $this->current_i18n[$culture];
  }

  public function setTranslatorI18nForCulture($object, $culture)
  {
    $this->current_i18n[$culture] = $object;
    $this->addTranslatorI18n($object);
  }


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseTranslator:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseTranslator::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


} // BaseTranslator
