	public function fromArray($arr, $keyType = BasePeer::TYPE_PHPNAME)
	{
		$keys = TranslatorCouplePeer::getFieldNames($keyType);

		if (array_key_exists($keys[0], $arr)) $this->setId($arr[$keys[0]]);
		if (array_key_exists($keys[1], $arr)) $this->setTranslatorId($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setSourceLang($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setTargetLang($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setTwoWay($arr[$keys[4]]);
		if (array_key_exists($keys[5], $arr)) $this->setCanInterpret($arr[$keys[5]]);
		if (array_key_exists($keys[6], $arr)) $this->setLevelInterpret($arr[$keys[6]]);
	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(TranslatorCouplePeer::DATABASE_NAME);

		if ($this->isColumnModified(TranslatorCouplePeer::ID)) $criteria->add(TranslatorCouplePeer::ID, $this->id);
		if ($this->isColumnModified(TranslatorCouplePeer::TRANSLATOR_ID)) $criteria->add(TranslatorCouplePeer::TRANSLATOR_ID, $this->translator_id);
		if ($this->isColumnModified(TranslatorCouplePeer::SOURCE_LANG)) $criteria->add(TranslatorCouplePeer::SOURCE_LANG, $this->source_lang);
		if ($this->isColumnModified(TranslatorCouplePeer::TARGET_LANG)) $criteria->add(TranslatorCouplePeer::TARGET_LANG, $this->target_lang);
		if ($this->isColumnModified(TranslatorCouplePeer::TWO_WAY)) $criteria->add(TranslatorCouplePeer::TWO_WAY, $this->two_way);
		if ($this->isColumnModified(TranslatorCouplePeer::CAN_INTERPRET)) $criteria->add(TranslatorCouplePeer::CAN_INTERPRET, $this->can_interpret);
		if ($this->isColumnModified(TranslatorCouplePeer::LEVEL_INTERPRET)) $criteria->add(TranslatorCouplePeer::LEVEL_INTERPRET, $this->level_interpret);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
	 */
	public function buildPkeyCriteria()
	{
		$criteria = new Criteria(TranslatorCouplePeer::DATABASE_NAME);

		$criteria->add(TranslatorCouplePeer::ID, $this->id);

		return $criteria;
	}

	/**
	 * Returns the primary key for this object (row).
	 * @return     int
	 */
	public function getPrimaryKey()
	{
		return $this->getId();
	}

	/**
	 * Generic method to set the primary key (id column).
	 *
	 * @param      int $key Primary key.
	 * @return     void
	 */
	public function setPrimaryKey($key)
	{
		$this->setId($key);
	}

	/**
	 * Sets contents of passed object to values from current object.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      object $copyObj An object of TranslatorCouple (or compatible) type.
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @throws     PropelException
	 */
	public function copyInto($copyObj, $deepCopy = false)
	{

		$copyObj->setTranslatorId($this->translator_id);

		$copyObj->setSourceLang($this->source_lang);

		$copyObj->setTargetLang($this->target_lang);

		$copyObj->setTwoWay($this->two_way);

		$copyObj->setCanInterpret($this->can_interpret);

		$copyObj->setLevelInterpret($this->level_interpret);


		$copyObj->setNew(true);

		$copyObj->setId(NULL); // this is a auto-increment column, so set to default value

	}

	/**
	 * Makes a copy of this object that will be inserted as a new row in table when saved.
	 * It creates a new object filling in the simple attributes, but skipping any primary
	 * keys that are defined for the table.
	 *
	 * If desired, this method can also make copies of all associated (fkey referrers)
	 * objects.
	 *
	 * @param      boolean $deepCopy Whether to also copy all rows that refer (by fkey) to the current row.
	 * @return     TranslatorCouple Clone of current object.
	 * @throws     PropelException
	 */
	public function copy($deepCopy = false)
	{
		// we use get_class(), because this might be a subclass
		$clazz = get_class($this);
		$copyObj = new $clazz();
		$this->copyInto($copyObj, $deepCopy);
		return $copyObj;
	}

	/**
	 * Returns a peer instance associated with this om.
	 *
	 * Since Peer classes are not to have any instance attributes, this method returns the
	 * same instance for all member of this class. The method could therefore
	 * be static, but this would prevent one from overriding the behavior.
	 *
	 * @return     TranslatorCouplePeer
	 */
	public function getPeer()
	{
		if (self::$peer === null) {
			self::$peer = new TranslatorCouplePeer();
		}
		return self::$peer;
	}

	/**
	 * Declares an association between this object and a Translator object.
	 *
	 * @param      Translator $v
	 * @return     TranslatorCouple The current object (for fluent API support)
	 * @throws     PropelException
	 */
	public function setTranslator(Translator $v = null)
	{
		if ($v === null) {
			$this->setTranslatorId(NULL);
		} else {
			$this->setTranslatorId($v->getId());
		}

		$this->aTranslator = $v;

		// Add binding for other direction of this n:n relationship.
		// If this object has already been added to the Translator object, it will not be re-added.
		if ($v !== null) {
			$v->addTranslatorCouple($this);
		}

		return $this;
	}


	/**
	 * Get the associated Translator object
	 *
	 * @param      PropelPDO Optional Connection object.
	 * @return     Translator The associated Translator object.
	 * @throws     PropelException
	 */
	public function getTranslator(PropelPDO $con = null)
	{
		if ($this->aTranslator === null && ($this->translator_id !== null)) {
			$c = new Criteria(TranslatorPeer::DATABASE_NAME);
			$c->add(TranslatorPeer::ID, $this->translator_id);
			$this->aTranslator = TranslatorPeer::doSelectOne($c, $con);
			/* The following can be used additionally to
			   guarantee the related object contains a reference
			   to this object.  This level of coupling may, however, be
			   undesirable since it could result in an only partially populated collection
			   in the referenced object.
			   $this->aTranslator->addTranslatorCouples($this);
			 */
		}
		return $this->aTranslator;
	}

	/**
	 * Resets all collections of referencing foreign keys.
	 *
	 * This method is a user-space workaround for PHP's inability to garbage collect objects
	 * with circular references.  This is currently necessary when using Propel in certain
	 * daemon or large-volumne/high-memory operations.
	 *
	 * @param      boolean $deep Whether to also clear the references on all associated objects.
	 */
	public function clearAllReferences($deep = false)
	{
		if ($deep) {
		} // if ($deep)

			$this->aTranslator = null;
	}


  public function __call($method, $arguments)
  {
    if (!$callable = sfMixer::getCallable('BaseTranslatorCouple:'.$method))
    {
      throw new sfException(sprintf('Call to undefined method BaseTranslatorCouple::%s', $method));
    }

    array_unshift($arguments, $this);

    return call_user_func_array($callable, $arguments);
  }


} // BaseTranslatorCouple
